def handle_number (journalists)
  puts ""
  puts "1 - combien y a t il de handle dans cette array?"
  sleep 4
  puts ""
  puts journalists.size.to_s + " journalistes, mon dieu qu'ils sont nombreux!"
puts ""
end

def short_handle (journalists)
  puts ""
  puts "2 - quel est le handle le plus court de cette liste?"
  sleep 4
  puts ""
puts "#{journalists.sort_by(&:length)[0]} est le plus rikiki des handle!"
puts ""
end

def five_caracters (journalists)
number = 0
journalists.each do |i|
  if (i.split("").length - 1) == 5
  number += 1
  end
end
puts ""
puts "3 - combien y a t il de handle contenant 5 caractères?"
sleep 4
puts ""
puts "il n'a que #{number} journalistes dont l'handle est doté de 5 caractères!!"
puts ""
end

def upcase (journalists)
number = 0
journalists.each do |i|
  if (i.split(""))[1] == (i.split(""))[1].upcase
  number += 1
  end
end
puts ""
puts "4 - Combien commencent par une majuscule"
sleep 4
puts ""
puts "et bien il y en a #{number} qui sont très fiers de leur handle ! "
puts ""
end

def Alpha (journalists)
  puts ""
  puts "5 - trie la liste de handle par ordre alphabétique"
  sleep 4
  puts ""
  puts journalists.sort_by(&:downcase)
  puts ""
end

def Sort_length (journalists)
  puts ""
  puts "6 - trie la liste de handle par taille des handle"
  sleep 4
  puts ""
  puts journalists.sort_by(&:length)
  puts ""
end

def position (journalists)
  puts ""
